package Day6;
public class Day6 {
    public static Integer tuningTrouble(){
        String s = "";
        for (int i = 0 ; i < s.length() - 4 ; i++) {
            if(hasAllUniqueCharacters(s.substring(i,i+4))){
                return i+4;
            }
        }
        return null;
    }

    public static Integer tuningTrouble2(){
        String s = "";
        for (int i = 0 ; i < s.length() - 14 ; i++) {
            if(hasAllUniqueCharacters(s.substring(i,i+14))){
                return i+14;
            }
        }
        return null;
    }
    private static boolean hasAllUniqueCharacters(String s){
        for (int i = 0; i < s.length(); i++) {
            for (int j = 0; j < s.length(); j++) {
                if( s.charAt(i) == s.charAt(j) && i != j){
                    return false;
                }
            }
        }
        return true;
    }
    public static void main(String[] args){
        System.out.println(tuningTrouble());
        System.out.println(tuningTrouble2());
    }
}
